use spring;

DROP TABLE used_car;
commit;

CREATE TABLE used_car (
    ucno INT AUTO_INCREMENT PRIMARY KEY,
    cname VARCHAR(50) NOT NULL,
    cnumber VARCHAR(50) NOT NULL,
    cmanufacturer VARCHAR(50) NOT NULL,
    cimg VARCHAR(255) NOT NULL,
    cyear YEAR NOT NULL,
    cmileage INT NOT NULL,
    carea VARCHAR(50) NOT NULL,
    cprice DECIMAL(10, 0) NOT NULL,
    cshortment VARCHAR(50) NOT NULL,
    cseller VARCHAR(50) NOT NULL,
    cseller_contact VARCHAR(50) NOT NULL,
    ccomment VARCHAR(1000) NOT NULL,
    ccolor VARCHAR(50) NOT NULL,
    cfuel_type VARCHAR(20) NOT NULL,
    ctransmission VARCHAR(20) NOT NULL,
    ccondition VARCHAR(20) NOT NULL,
    view_count INT DEFAULT 0,
    status VARCHAR(20) DEFAULT '판매중',
    creg_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);




select * from used_car;
SELECT * FROM used_car ORDER BY ucno DESC;
